require 'spec_helper'

RSpec.describe VatInfo::Utils do
  let(:message) do
    VatInfo::Message.new({ dic_popl: 'CZ72080043',
                       id_provoz: '181',
                       id_pokl: '00/2535/CN58',
                       porad_cis: '0/2482/IE25',
                       dat_trzby: Time.parse('2016-12-07T22:01:00+01:00').strftime(VatInfo::Message::DATE_FORMAT),
                       celk_trzba: '87988.00' })
  end

  describe '::create_pkp' do
    it 'creates pkp' do
      certificate = OpenSSL::PKCS12.new(File.open('spec/fixtures/EET_CA1_Playground-CZ00000019.p12'), 'eet')

      expected_pkp = ''

      expect(VatInfo::Utils.create_pkp(message, certificate)) .to eq expected_pkp
    end
  end

  describe '::create_bkp' do
    it 'creates bkp from pkp' do
      pkp = ''

      expect(VatInfo::Utils.create_bkp(pkp)).to eq '8AE6E1D6-B2BF469F-B04C3516-B020E3E0-3FCEC26A'
    end
  end

  describe '::erialize_pkp_data' do
    it "serializes pkp data into string divided by '|'" do
      expect(VatInfo::Utils.serialize_pkp_data(message))
        .to eq "CZ72080043|181|00/2535/CN58|0/2482/IE25|2016-12-07T22:01:00+01:00|87988.00"
    end
  end

  describe '::sign' do
    it 'signs message with WS security' do
      certificate = OpenSSL::PKCS12.new(File.open('spec/fixtures/EET_CA1_Playground-CZ00000019.p12'), 'eet')

      msg = Nokogiri::XML(File.open('spec/fixtures/unsigned_message.xml'))
      signed_message = VatInfo::Utils.sign(msg.to_xml, certificate)

      expect(signed_message).to eq File.read('spec/fixtures/signed_message.xml')
    end
  end
end
